import React, { useState } from "react";
import "./Person.css";
import axios from "axios";


const Person = ({ onAdd }) => {
  const [name, setName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);

  const handleAdd = async () => {
    if (name.trim() === "" || phoneNumber.trim() === "") {
      console.log("Name and Phone Number cannot be empty");
      return;
    }

    try {
      // Axios ile GET isteği yaparak mevcut kişileri alın
      const response = await axios.get("http://localhost:5000/get_contacts");
      const existingContacts = response.data;

      // Yeni kişi için bir ID belirle (mevcut kişilerin sayısına bir ekleyerek)
      const newId = existingContacts.length + 1;

      const newContact = {
        id: newId,
        name: name,
        phoneNumber: phoneNumber
      };

      // Axios ile POST isteği yaparak Flask API'ye yeni kişiyi ekleyin
      const postResponse = await axios.post("http://localhost:5000/add_contact", newContact);

      // Başarılı bir şekilde ekledikten sonra, onAdd fonksiyonunu çağırarak React tarafındaki state'i güncelleyin
      onAdd(postResponse.data);

      // State'i temizle
      setName("");
      setPhoneNumber("");
    } catch (error) {
      console.error("Kişi eklenirken hata oluştu:", error);
    }
  };
  

  const handleEdit = () => {
    if (name.trim() === "" || phoneNumber.trim() === "") {
      console.log("İsim ve Telefon Numarası boş olamaz");
      return;
    }

    const editedContact = {
      name: name,
      phoneNumber: phoneNumber
    };

   // onEdit(editedContact);

   // onCancel();
  };
  const handleRemove = () => {};

  return (
    <div>
      {isEditing ? (
        <div>
          <h2>Edit Person</h2>
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Phone Number"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />
          <button onClick={handleEdit}>Edit</button>
        </div>
      ) : (
        <div className="add-person-container">
  <h2>Add New Person</h2>
  <input
    type="text"
    placeholder="Name"
    value={name}
    onChange={(e) => setName(e.target.value)}
  />
  <input
    type="text"
    placeholder="Phone Number"
    value={phoneNumber}
    onChange={(e) => setPhoneNumber(e.target.value)}
  />
  <button onClick={handleAdd}>Add</button>
</div>
      )}
      {selectedContact && (
        <div>
          <h2>Selected Person</h2>
          <p>Name: {selectedContact.name}</p>
          <p>Phone Number: {selectedContact.phoneNumber}</p>
          <button onClick={handleRemove}>Remove</button>
          {}
        </div>
      )}
    </div>
  );
};

export default Person;
