import React, { useState, useEffect } from "react";
import "./Edit.css";
const Edit = ({ contact, onEdit, onClose }) => {
  const [editedName, setEditedName] = useState(contact ? contact.name : "");
  const [editedNumber, setEditedNumber] = useState(
    contact ? contact.phoneNumber : ""
  );

  useEffect(() => {
    // Eğer bir temas varsa, onun bilgilerini giriş alanlarına yerleştir
    if (contact) {
      setEditedName(contact.name);
      setEditedNumber(contact.phoneNumber);
    }
  }, [contact]);

  const handleEdit = () => {
    // Burada düzenleme işlemini gerçekleştir
    // Örnek olarak, burada bir API çağrısı yapılabilir veya yerel state güncellenebilir
    // Ancak, bu örnekte sadece bir fonksiyon çağrısı yaptık
    if (contact) {
      onEdit(contact.id, editedName, editedNumber);
    } else {
      onEdit(editedName, editedNumber);
    }
  };

  return (
    <div className="edit-form">
      <h2>Edit Contact</h2>
      <label>
        Name:
        <input
          type="text"
          value={editedName}
          onChange={(e) => setEditedName(e.target.value)}
        />
      </label>
      <label>
        Phone Number:
        <input
          type="text"
          value={editedNumber}
          onChange={(e) => setEditedNumber(e.target.value)}
        />
      </label>
      <button onClick={handleEdit}>Save Changes</button>
      <button onClick={onClose}>Cancel</button>
    </div>
  );
};

export default Edit;
