import React, { useState, useEffect } from "react";
import "./Edit.css";
import axios from "axios";


const Edit = ({ contact, onEdit, onClose }) => {
  const [editedName, setEditedName] = useState(contact ? contact.name : "");
  const [editedNumber, setEditedNumber] = useState(
    contact ? contact.phoneNumber : ""
  );

  useEffect(() => {
  
    if (contact) {
      setEditedName(contact.name);
      setEditedNumber(contact.phoneNumber);
    }
  }, [contact]);

  const handleEdit = async () => {
    try {
      if (contact) {
       
        await axios.put(`http://localhost:3005/edit-contact/${contact.id}`, {
          editedName,
          editedNumber,
        });
        onEdit(contact.id, editedName, editedNumber);
      } else {
      
        const response = await axios.post("http://localhost:3005/add-contact", {
          editedName,
          editedNumber,
        });
        onEdit(response.data.id, editedName, editedNumber);
      }
    } catch (error) {
      console.error("Error while editing contact:", error);
    } finally {
      onClose(); // Düzenleme işlemi tamamlandığında pencereyi kapat
    }
  };

  return (
    <div className="edit-form">
      <h2>Edit Contact</h2>
      <label>
        Name:
        <input
          type="text"
          value={editedName}
          onChange={(e) => setEditedName(e.target.value)}
        />
      </label>
      <label>
      Category:
        <input
          type="text"
          value={editedNumber}
          onChange={(e) => setEditedNumber(e.target.value)}
        />
      </label>
      <label>
        Description:
        <input
          type="text"
          value={editedNumber}
          onChange={(e) => setEditedNumber(e.target.value)}
        />
      </label>
      <label>
      Price:
        <input
          type="text"
          value={editedNumber}
          onChange={(e) => setEditedNumber(e.target.value)}
        />
      </label>

      <label>
      Discount Price:
        <input
          type="text"
          value={editedNumber}
          onChange={(e) => setEditedNumber(e.target.value)}
        />
      </label>
      <button onClick={handleEdit}>Save Changes</button>
      <button onClick={onClose}>Cancel</button>
    </div>
  );
};

export default Edit;
