import React, { useState, useEffect } from "react";
import axios from "axios";
import Person from "./Person";
//import Edit from "./Edit";
import "./Main.css";

const Main = () => {
  const [searchName, setSearchName] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [selectedContact, setSelectedContact] = useState(null);
  const [contacts, setContacts] = useState([]);
  const [allContacts, setAllContacts] = useState([]);


  const filteredContacts = contacts.filter((contact) =>
    contact.name.toLowerCase().includes(searchName.toLowerCase()),
  );

  useEffect(() => {
    // Flask API'ından tüm kişi listesini çek
    const fetchAllContacts = async () => {
      try {
        const response = await axios.get("http://localhost:5000/contacts");
        setContacts(response.data);
        setAllContacts(response.data);
      } catch (error) {
        console.error("Kişileri çekerken hata oluştu:", error);
      }
    };
  
    fetchAllContacts();
  }, []);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/contacts?search=${searchName}`);
      setContacts(response.data);
    } catch (error) {
      console.error("Kişileri ararken hata oluştu:", error);
      // Hata durumunda, orijinal kişi listesini geri yükle
      setContacts(allContacts);
    }
  };
  

  const handleEdit = (id) => {
    const editedContact = contacts.find((contact) => contact.id === id);
    setSelectedContact(editedContact);
    setIsEditing(true);
  };

  const closeEditScreen = () => {
    setIsEditing(false);
    setSelectedContact(null);
  };

  const removeContact = (id) => {
    const updatedContacts = contacts.filter((contact) => contact.id !== id);
    setContacts(updatedContacts);
  };

  return (
    <div className="container">
      <div className="left-panel">
        <div className="input-container">
          <input
            type="text"
            placeholder="Search by name"
            value={searchName}
            onChange={(e) => setSearchName(e.target.value)}
          />
          <button onClick={handleSearch}>Find</button>
        </div>

        <div className="right-panel">
          {filteredContacts.map((contact) => (
            <div key={contact.id} className="contact">
              <p>Name: {contact.name}</p>
              <p>Phone Number: {contact.phoneNumber}</p>
              <button onClick={() => removeContact(contact.id)}>Remove</button>
              <button onClick={() => handleEdit(contact.id)}>Edit</button>
              {}
            </div>
          ))}
        </div>
      </div>
      {!isEditing && (
        <Person
          onAdd={(newContact) => {
            setContacts([...contacts, newContact]);
            setIsEditing(false);
          }}
        />
      )}
      {isEditing && (
        <div>
          {selectedContact ? (
            <Edit
              contact={selectedContact}
              onEdit={(id, editedName, editedNumber) => {
                setIsEditing(false);
              }}
              onClose={() => setIsEditing(false)}
            />
          ) : (
            <Edit
              onEdit={(newName, newNumber) => {
                setIsEditing(false);
              }}
              onClose={() => setIsEditing(false)}
            />
          )}
        </div>
      )}
    </div>
  );
};

export default Main;
